import{_ as l,c as i,o as e,ag as r}from"./chunks/framework.DK1-H3E1.js";const b=JSON.parse('{"title":"Java程序员面试学习大纲","description":"","frontmatter":{"order":1},"headers":[],"relativePath":"interview/java-interview-outline.md","filePath":"interview/java-interview-outline.md","lastUpdated":1769531538000}'),t={name:"interview/java-interview-outline.md"};function o(h,a,n,u,s,d){return e(),i("div",null,a[0]||(a[0]=[r('<h1 id="java程序员面试学习大纲" tabindex="-1">Java程序员面试学习大纲 <a class="header-anchor" href="#java程序员面试学习大纲" aria-label="Permalink to &quot;Java程序员面试学习大纲&quot;">​</a></h1><h2 id="一、java基础" tabindex="-1">一、Java基础 <a class="header-anchor" href="#一、java基础" aria-label="Permalink to &quot;一、Java基础&quot;">​</a></h2><h3 id="_1-1-语言特性" tabindex="-1">1.1 语言特性 <a class="header-anchor" href="#_1-1-语言特性" aria-label="Permalink to &quot;1.1 语言特性&quot;">​</a></h3><ul><li>面向对象编程（封装、继承、多态、抽象）</li><li>Java 8+ 新特性（Lambda、Stream、Optional、函数式接口）</li><li>泛型机制</li><li>反射机制</li><li>注解（Annotation）</li><li>异常处理机制</li></ul><h3 id="_1-2-集合框架" tabindex="-1">1.2 集合框架 <a class="header-anchor" href="#_1-2-集合框架" aria-label="Permalink to &quot;1.2 集合框架&quot;">​</a></h3><ul><li>List、Set、Map 的实现原理</li><li>ArrayList vs LinkedList</li><li>HashMap、ConcurrentHashMap 源码分析</li><li>TreeMap、LinkedHashMap</li><li>线程安全的集合类</li></ul><h3 id="_1-3-io-nio" tabindex="-1">1.3 IO/NIO <a class="header-anchor" href="#_1-3-io-nio" aria-label="Permalink to &quot;1.3 IO/NIO&quot;">​</a></h3><ul><li>字节流、字符流</li><li>NIO、AIO</li><li>文件操作</li></ul><h2 id="二、并发编程" tabindex="-1">二、并发编程 <a class="header-anchor" href="#二、并发编程" aria-label="Permalink to &quot;二、并发编程&quot;">​</a></h2><h3 id="_2-1-线程基础" tabindex="-1">2.1 线程基础 <a class="header-anchor" href="#_2-1-线程基础" aria-label="Permalink to &quot;2.1 线程基础&quot;">​</a></h3><ul><li>线程的创建方式</li><li>线程生命周期</li><li>线程同步机制（synchronized、volatile）</li><li>wait/notify/notifyAll</li></ul><h3 id="_2-2-juc包" tabindex="-1">2.2 JUC包 <a class="header-anchor" href="#_2-2-juc包" aria-label="Permalink to &quot;2.2 JUC包&quot;">​</a></h3><ul><li>Lock接口（ReentrantLock、ReadWriteLock）</li><li>并发集合（ConcurrentHashMap、CopyOnWriteArrayList）</li><li>线程池（ThreadPoolExecutor、Executors）</li><li>原子类（AtomicInteger、AtomicReference等）</li><li>CountDownLatch、CyclicBarrier、Semaphore</li><li>CompletableFuture</li></ul><h3 id="_2-3-并发设计模式" tabindex="-1">2.3 并发设计模式 <a class="header-anchor" href="#_2-3-并发设计模式" aria-label="Permalink to &quot;2.3 并发设计模式&quot;">​</a></h3><ul><li>生产者消费者模式</li><li>线程池模式</li><li>线程本地存储（ThreadLocal）</li></ul><h2 id="三、jvm" tabindex="-1">三、JVM <a class="header-anchor" href="#三、jvm" aria-label="Permalink to &quot;三、JVM&quot;">​</a></h2><h3 id="_3-1-内存模型" tabindex="-1">3.1 内存模型 <a class="header-anchor" href="#_3-1-内存模型" aria-label="Permalink to &quot;3.1 内存模型&quot;">​</a></h3><ul><li>堆、栈、方法区、程序计数器</li><li>堆内存分区（新生代、老年代）</li><li>对象创建过程</li><li>垃圾回收算法（标记清除、标记复制、标记整理）</li><li>垃圾回收器（Serial、Parallel、CMS、G1、ZGC）</li></ul><h3 id="_3-2-性能调优" tabindex="-1">3.2 性能调优 <a class="header-anchor" href="#_3-2-性能调优" aria-label="Permalink to &quot;3.2 性能调优&quot;">​</a></h3><ul><li>JVM参数调优</li><li>内存泄漏排查</li><li>GC日志分析</li><li>性能监控工具（jstat、jmap、jstack、VisualVM、Arthas）</li></ul><h3 id="_3-3-类加载机制" tabindex="-1">3.3 类加载机制 <a class="header-anchor" href="#_3-3-类加载机制" aria-label="Permalink to &quot;3.3 类加载机制&quot;">​</a></h3><ul><li>类加载过程（加载、链接、初始化）</li><li>双亲委派模型</li><li>自定义类加载器</li></ul><h2 id="四、框架技术" tabindex="-1">四、框架技术 <a class="header-anchor" href="#四、框架技术" aria-label="Permalink to &quot;四、框架技术&quot;">​</a></h2><h3 id="_4-1-spring框架" tabindex="-1">4.1 Spring框架 <a class="header-anchor" href="#_4-1-spring框架" aria-label="Permalink to &quot;4.1 Spring框架&quot;">​</a></h3><ul><li>IOC容器原理</li><li>AOP实现原理</li><li>Spring Bean生命周期</li><li>Spring事务管理</li><li>Spring MVC原理</li><li>Spring Boot自动配置原理</li><li>Spring Cloud微服务组件</li></ul><h3 id="_4-2-mybatis" tabindex="-1">4.2 MyBatis <a class="header-anchor" href="#_4-2-mybatis" aria-label="Permalink to &quot;4.2 MyBatis&quot;">​</a></h3><ul><li>MyBatis工作原理</li><li>动态SQL</li><li>缓存机制（一级缓存、二级缓存）</li><li>插件机制</li></ul><h3 id="_4-3-其他框架" tabindex="-1">4.3 其他框架 <a class="header-anchor" href="#_4-3-其他框架" aria-label="Permalink to &quot;4.3 其他框架&quot;">​</a></h3><ul><li>Redis客户端（Jedis、Lettuce）</li><li>消息队列（RabbitMQ、Kafka）</li><li>定时任务（Quartz、XXL-Job）</li></ul><h2 id="五、数据库" tabindex="-1">五、数据库 <a class="header-anchor" href="#五、数据库" aria-label="Permalink to &quot;五、数据库&quot;">​</a></h2><h3 id="_5-1-mysql" tabindex="-1">5.1 MySQL <a class="header-anchor" href="#_5-1-mysql" aria-label="Permalink to &quot;5.1 MySQL&quot;">​</a></h3><ul><li>索引原理（B+树）</li><li>事务隔离级别</li><li>MVCC机制</li><li>锁机制（表锁、行锁、间隙锁）</li><li>SQL优化</li><li>分库分表</li><li>主从复制、读写分离</li></ul><h3 id="_5-2-redis" tabindex="-1">5.2 Redis <a class="header-anchor" href="#_5-2-redis" aria-label="Permalink to &quot;5.2 Redis&quot;">​</a></h3><ul><li>数据结构（String、Hash、List、Set、ZSet）</li><li>持久化机制（RDB、AOF）</li><li>缓存穿透、缓存击穿、缓存雪崩</li><li>分布式锁实现</li><li>集群模式（主从、哨兵、Cluster）</li></ul><h2 id="六、分布式系统" tabindex="-1">六、分布式系统 <a class="header-anchor" href="#六、分布式系统" aria-label="Permalink to &quot;六、分布式系统&quot;">​</a></h2><h3 id="_6-1-分布式理论" tabindex="-1">6.1 分布式理论 <a class="header-anchor" href="#_6-1-分布式理论" aria-label="Permalink to &quot;6.1 分布式理论&quot;">​</a></h3><ul><li>CAP定理</li><li>BASE理论</li><li>一致性协议（2PC、3PC、Paxos、Raft）</li></ul><h3 id="_6-2-分布式组件" tabindex="-1">6.2 分布式组件 <a class="header-anchor" href="#_6-2-分布式组件" aria-label="Permalink to &quot;6.2 分布式组件&quot;">​</a></h3><ul><li>服务注册与发现（Nacos、Eureka、Consul）</li><li>配置中心</li><li>网关（Spring Cloud Gateway、Zuul）</li><li>服务调用（Feign、Ribbon）</li><li>熔断降级（Hystrix、Sentinel）</li><li>分布式事务（Seata、TCC、Saga）</li></ul><h3 id="_6-3-分布式锁" tabindex="-1">6.3 分布式锁 <a class="header-anchor" href="#_6-3-分布式锁" aria-label="Permalink to &quot;6.3 分布式锁&quot;">​</a></h3><ul><li>基于Redis实现</li><li>基于Zookeeper实现</li><li>基于数据库实现</li></ul><h3 id="_6-4-消息队列" tabindex="-1">6.4 消息队列 <a class="header-anchor" href="#_6-4-消息队列" aria-label="Permalink to &quot;6.4 消息队列&quot;">​</a></h3><ul><li>Kafka架构原理</li><li>RabbitMQ工作模式</li><li>消息可靠性保证</li><li>消息顺序性保证</li></ul><h2 id="七、系统设计" tabindex="-1">七、系统设计 <a class="header-anchor" href="#七、系统设计" aria-label="Permalink to &quot;七、系统设计&quot;">​</a></h2><h3 id="_7-1-设计原则" tabindex="-1">7.1 设计原则 <a class="header-anchor" href="#_7-1-设计原则" aria-label="Permalink to &quot;7.1 设计原则&quot;">​</a></h3><ul><li>SOLID原则</li><li>设计模式（单例、工厂、观察者、策略、责任链等）</li><li>高并发系统设计</li><li>高可用系统设计</li></ul><h3 id="_7-2-常见场景设计" tabindex="-1">7.2 常见场景设计 <a class="header-anchor" href="#_7-2-常见场景设计" aria-label="Permalink to &quot;7.2 常见场景设计&quot;">​</a></h3><ul><li>秒杀系统</li><li>分布式ID生成</li><li>限流算法（令牌桶、漏桶、滑动窗口）</li><li>负载均衡算法</li><li>短链接系统</li><li>搜索引擎设计</li></ul><h2 id="八、项目经验总结" tabindex="-1">八、项目经验总结 <a class="header-anchor" href="#八、项目经验总结" aria-label="Permalink to &quot;八、项目经验总结&quot;">​</a></h2><h3 id="_9-1-项目背景" tabindex="-1">9.1 项目背景 <a class="header-anchor" href="#_9-1-项目背景" aria-label="Permalink to &quot;9.1 项目背景&quot;">​</a></h3><ul><li>租车门店基础信息维护系统</li><li>供应商后台系统</li><li>系统架构设计</li><li>技术选型理由</li></ul><h3 id="_9-2-技术难点" tabindex="-1">9.2 技术难点 <a class="header-anchor" href="#_9-2-技术难点" aria-label="Permalink to &quot;9.2 技术难点&quot;">​</a></h3><ul><li>高并发场景处理</li><li>数据一致性保证</li><li>性能优化经验</li><li>问题排查与解决</li></ul><h3 id="_9-3-项目亮点" tabindex="-1">9.3 项目亮点 <a class="header-anchor" href="#_9-3-项目亮点" aria-label="Permalink to &quot;9.3 项目亮点&quot;">​</a></h3><ul><li>系统优化成果</li><li>技术方案创新</li><li>业务价值体现</li></ul><h2 id="九、其他技能" tabindex="-1">九、其他技能 <a class="header-anchor" href="#九、其他技能" aria-label="Permalink to &quot;九、其他技能&quot;">​</a></h2><h3 id="_9-1-开发工具" tabindex="-1">9.1 开发工具 <a class="header-anchor" href="#_9-1-开发工具" aria-label="Permalink to &quot;9.1 开发工具&quot;">​</a></h3><ul><li>Git版本控制</li><li>Maven/Gradle构建工具</li><li>IDEA使用技巧</li><li>Linux常用命令</li></ul><h3 id="_9-2-网络基础" tabindex="-1">9.2 网络基础 <a class="header-anchor" href="#_9-2-网络基础" aria-label="Permalink to &quot;9.2 网络基础&quot;">​</a></h3><ul><li>HTTP/HTTPS协议</li><li>TCP/IP协议</li><li>OSI七层模型</li><li>三次握手、四次挥手</li></ul><h3 id="_9-3-安全" tabindex="-1">9.3 安全 <a class="header-anchor" href="#_9-3-安全" aria-label="Permalink to &quot;9.3 安全&quot;">​</a></h3><ul><li>SQL注入防护</li><li>XSS攻击防护</li><li>CSRF攻击防护</li><li>接口鉴权（JWT、OAuth2）</li></ul><h2 id="十、面试准备" tabindex="-1">十、面试准备 <a class="header-anchor" href="#十、面试准备" aria-label="Permalink to &quot;十、面试准备&quot;">​</a></h2><h3 id="_10-1-简历准备" tabindex="-1">10.1 简历准备 <a class="header-anchor" href="#_10-1-简历准备" aria-label="Permalink to &quot;10.1 简历准备&quot;">​</a></h3><ul><li>项目经验描述</li><li>技术栈梳理</li><li>成果量化</li></ul><h3 id="_10-2-面试技巧" tabindex="-1">10.2 面试技巧 <a class="header-anchor" href="#_10-2-面试技巧" aria-label="Permalink to &quot;10.2 面试技巧&quot;">​</a></h3><ul><li>自我介绍准备</li><li>常见问题准备</li><li>技术问题回答思路</li><li>项目介绍话术</li></ul><h3 id="_10-3-模拟面试" tabindex="-1">10.3 模拟面试 <a class="header-anchor" href="#_10-3-模拟面试" aria-label="Permalink to &quot;10.3 模拟面试&quot;">​</a></h3><ul><li>系统设计题练习</li><li>技术深度问题准备</li></ul><hr><h2 id="学习建议" tabindex="-1">学习建议 <a class="header-anchor" href="#学习建议" aria-label="Permalink to &quot;学习建议&quot;">​</a></h2><ol><li><strong>优先级排序</strong>：根据目标岗位要求，优先学习核心技能</li><li><strong>理论与实践结合</strong>：不仅要理解原理，还要动手实践</li><li><strong>项目经验梳理</strong>：深入思考项目中遇到的问题和解决方案</li><li><strong>技术深度</strong>：选择1-2个方向深入研究，形成技术亮点</li></ol><h2 id="学习时间规划" tabindex="-1">学习时间规划 <a class="header-anchor" href="#学习时间规划" aria-label="Permalink to &quot;学习时间规划&quot;">​</a></h2><ul><li><strong>第1-2周</strong>：Java基础、集合框架、并发编程基础</li><li><strong>第3-4周</strong>：JVM、Spring框架深入</li><li><strong>第5-6周</strong>：数据库、Redis、分布式基础</li><li><strong>第7-8周</strong>：系统设计、项目经验总结</li><li><strong>第9-10周</strong>：模拟面试、查漏补缺</li></ul><hr><p><em>最后更新时间：2024年</em></p>',76)]))}const q=l(t,[["render",o]]);export{b as __pageData,q as default};
